table.sql:
CREATE TABLE IF NOT EXISTS `global_search_modules` (
					`module`  varchar(250) NOT NULL ,
					`active`  int(1) NULL DEFAULT NULL ,
					`sequence`  int(3) NULL,
					PRIMARY KEY (`module`)
					);

CREATE TABLE IF NOT EXISTS `global_search_fields` (
				`id`  int(11) NOT NULL AUTO_INCREMENT,
				`module`  varchar(50) NULL ,
				`fieldname`  varchar(250) NULL ,
				PRIMARY KEY (`id`)
				);

CREATE TABLE IF NOT EXISTS `global_search_method` (
			`search_method`  varchar(50) NULL 
			)
			;

ALTER TABLE `global_search_fields`
					MODIFY COLUMN `fieldname`  text NULL,
					MODIFY COLUMN `fieldname_show`  text NULL;

ALTER TABLE `global_search_quick_search_settings`
					MODIFY COLUMN `search_fields`  text NULL,
					MODIFY COLUMN `show_fields`  text NULL;
